import React from 'react';
import Restaurant from '../Restaurant/Restaurant';
import SmallSearchBar from '../SmallSearchBar/SmallSearchBar';
import Yelp from '../../util/Yelp';

class ReviewPage extends React.Component {
    constructor(props) {
        super(props);
        let str = localStorage.getItem("new-tab-data");
        if(str) {
            let res = JSON.parse(str);
            this.state = {
                restaurant: res,
                isLoading: false
            }
            localStorage.removeItem("new-tab-data");
        } else {
            this.state = {
                restaurant: {},
                isLoading: false
            }
        }
    }

    render() {
        return (
            <div>
                <SmallSearchBar/>
                {this.state.isLoading ? <h1> Loading... </h1> : 
                <div>
                <Restaurant restaurant = {this.state.restaurant} hasClick = {false}/></div>}
            </div>
        );
    }

    componentDidMount() {
        if(this.state.restaurant) {
            this.setState({isLoading: true});
            let queryString = window.location.search;
            let urlParams = new URLSearchParams(queryString);
            let id = urlParams.get('id');
            console.log(id);
            Yelp.searchById(id).then(restaurants => {
                this.setState({restaurant: restaurants, isLoading: false});
            });
        }
    }
}


export default ReviewPage;
