import React from 'react';
import './SearchResults.css';
import RestaurantList from '../RestaurantList/RestaurantList';
import SearchBar from '../SearchBar/SearchBar';
import Yelp from '../../util/Yelp';


class SearchResults extends React.Component {
    constructor(props) {
        super(props);
        const queryString = this.props.location.search;
        const urlParams = new URLSearchParams(queryString);
        const term = urlParams.get('term');
        const location = urlParams.get('location');

        this.state = {
            term: term,
            location: location,
            restaurants: []
        };
        this.searchYelp = this.searchYelp.bind(this);
    }

    searchYelp(term, location) {
        Yelp.search(term, location).then(restaurants => {
            this.setState({restaurants: restaurants});

        });
    }

    render() {


        return (
            <div className="SearchResults">
                <h1> food-reviewer </h1>
                <h1> search results page </h1>
                <SearchBar onChange = {this.searchYelp}/>
                <h1> term: {this.state.term}</h1>
                <h1> location: {this.state.location} </h1>
                <h1> restaurants: {this.state.restaurants}</h1>
            </div>
        );
    }
}

export default SearchResults;
