import React from 'react';
const apiKey = 'l_V052s4yME-K4R0NxkXG70ZUaeDFjqpDj5FMFNv2mfhIfxytoTgTPobcN5bdijqpSwPd3CeG124Bp7_hBLaMJEF1GocsRAjaXfe50yMGjqQBMfxW-HV8PCJcezCXnYx';
//need to encrypt this


class Yelp extends React.Component {
    search(term, location) {
        fetch(`https://cors-anywhere.herokuapp.com/https://api.yelp.com/v3/businesses/search?term=${term}&location=${location}`
        , {headers: {Authorization: `Bearer: ${apiKey}`}}).then(response => {
            return response.json();
        }).then(jsonResponse => {
            if(jsonResponse.businesses) {
                return jsonResponse.businesses.map(business => {
                    return {id: business.id,
                        phone: business.phone,
                        name: business.name,
                        url: business.url,
                        image_url: business.image_url,
                        city: business.location.city,
                        state: business.location.state,
                        zipCode: business.location.zip_code,
                        country: business.location.country,
                        address: business.location.address1,
                        category: business.categories.title
                    };
                });
            } else {
                return []; //return empty array if no businesses found
            }
        });
    }

}

export default Yelp;
