import React from 'react';
import './SmallSearchBar.css';
import { Link, withRouter } from 'react-router-dom';
import MapQuest from '../../util/MapQuest';

const apiKey = 'u5mrF79LEqZx7JFwOyifoKT2a3ILGvRE';

class SmallSearchBar extends React.Component {
    constructor(props) {
		super(props);
		let location = '';
		/*
		if(navigator.geolocation) {
			//let loc = navigator.geolocation.getCurrentPosition(MapQuest.reverse);
			//const loc = {city: 'city', state: 'state', country: 'country'};
			location = navigator.geolocation.getCurrentPosition(MapQuest.reverse);
			//console.log(location);
		} 
		*/
		this.state = {
			term: '',
			location: location
		};
		//console.log(this.state.location);
		this.handleTermChange = this.handleTermChange.bind(this);
		this.handleLocationChange = this.handleLocationChange.bind(this);
		this.handleSearch = this.handleSearch.bind(this);
	}

    handleTermChange(e) {
		this.setState({term: e.target.value});
    }
    handleLocationChange(e) {
		this.setState({location: e.target.value});
	}
	handleSearch(e) {

		this.props.handleSearch(this.state.term, this.state.location);
		e.preventDefault();
		this.props.history.push(`/search?term=${this.state.term}&location=${this.state.location}`); //redirect url
	}
    render() {
	return (
		<div className = "SmallSearchBar-container">
			<div className = "bar-container">
			<Link className = "logo-container" to = '/' style={{ textDecoration: 'none' }}>
				<div className = "logo-text">
					<h3> food-reviewer </h3>
				</div>
			</Link>
			<form onSubmit = {this.handleSearch}>
				<div >
					<input className = "SmallSearchBar-fields" placeholder = "Search by business" onChange = {this.handleTermChange}/>
					<input className = "SmallSearchBar-fields" placeholder = "Search by location" value = {this.state.location} onChange = {this.handleLocationChange} />
					<input className = "SmallSearchBar-submit" type = 'submit' value = 'Search'/>
				</div>
			</form>
			</div>
	    </div>
	);
	}

	componentDidMount() {
		if(navigator.geolocation) {
			navigator.geolocation.getCurrentPosition( (pos) => {
				const lat = pos.coords.latitude;
        		const long = pos.coords.longitude;
        		return fetch(`https://www.mapquestapi.com/geocoding/v1/reverse?key=${apiKey}&location=${lat}%2C${long}&outFormat=json&thumbMaps=false`)

        		.then(jsonResponse => {
            		const city = jsonResponse.results[0].locations[0].adminArea5;
            		const state = jsonResponse.results[0].locations[0].adminArea3;
					const country = jsonResponse.results[0].locations[0].adminArea1;
					console.log(`${city}, ${state}, ${country}`);
					return `${city}, ${state}, ${country}`;
				})
				.then(location => this.setState({location: location}))
				.catch(error => {
					console.log(error);
					return {};
				});
			}
			);
		} 
	}


}

export default withRouter(SmallSearchBar);
